import mmap  
import os   
import struct 

#打开文件，映射到内存，返回映射文件
def memory_map(filename, f_Filesize = 1024, f_access = mmap.ACCESS_WRITE,ftagname = 'share_mmap'): 
    fd = os.open(filename,os.O_RDWR)  
    return mmap.mmap(fd,f_Filesize,access=f_access,tagname = ftagname) 
	
def memory_map(filename, f_access = mmap.ACCESS_WRITE,ftagname = 'share_mmap'): 
    size = os.path.getsize(filename)  
    fd = os.open(filename,os.O_RDWR)  
    return mmap.mmap(fd,size,access=f_access,tagname = ftagname)
	
	
#映射内存文件
mmapfile = memory_map('d:\\test.txt',1024)

